beforeSaveCustomScript:
name = feedbackFormID; // formula to replace the by espocrm autogenerated random sequence for a more logical case ID 

// adding a datetime closed at whenever the status of the case is closed.
if(entity\isAttributeChanged('status') && status == 'closed'){
    datetimeClosed = datetime\now()
};

if(entity\isAttributeChanged('status') && status == 'in_progress'){
    datetimeClosed = null;
};

// Calculate days difference betweeen creating and closing feedback
daysDifferenceBetweenCreatedClosed = datetime\diff(datetimeClosed,createdAt,'hours')/24;


datetimeFeedbackCollectionStarted = ifThen(
    datetimeFeedbackCollectionStartedRaw != null,
    string\replace(string\substring(datetimeFeedbackCollectionStartedRaw, 0, 19), 'T', ' '),
    null
); // This line takes the first 19 characters from the raw datetime (removing milliseconds and timezone), replaces the 'T' with a space, and stores the result in datetimeFeedbackCollectionStarted in YYYY-MM-DD HH:MM:SS format.

datetimeFeedbackCollectionEnded = ifThen(
    datetimeFeedbackCollectionEndedRaw != null,
    string\replace(string\substring(datetimeFeedbackCollectionEndedRaw, 0, 19), 'T', ' '),
    null
); // This line takes the first 19 characters from the raw datetime (removing milliseconds and timezone), replaces the 'T' with a space, and stores the result in datetimeFeedbackCollectionStarted in YYYY-MM-DD HH:MM:SS format.

// This line takes the first 19 characters from the raw datetime (removing milliseconds and timezone), replaces the 'T' with a space, and stores the result in datetimeFeedbackCollectionEnded in YYYY-MM-DD HH:MM:SS format.
datetimeReceivedByKoboToolboxServer = ifThen(
    datetimeReceivedByKoboToolboxServerRaw != null,
    string\replace(string\substring(datetimeReceivedByKoboToolboxServerRaw, 0, 19), 'T', ' '),
    null
); // This line takes the first 19 characters from the raw datetime (removing milliseconds and timezone), replaces the 'T' with a space, and stores the result in datetimeReceivedByKoboToolboxServer in YYYY-MM-DD HH:MM:SS format.

// datetimeReceived = createdAt; // This line takes the created at datetime in EspoCRM as 


ifThen(
   assignedUserId != null && status == 'open',             // // someone has been assigned or re-assigned and current status is 'Open'
   status = 'in_progress'          // then set the status to 'In Progress'
);

// GPS data transformation
$gpsParts = string\split(gpsCoordinates, ' '); // Split the geopoint string into an array using a space as the separator
gpsLatitude = array\at($gpsParts, 0); // Extract the latitude (first part)
gpsLongitude = array\at($gpsParts, 1); // Extract the longitude (second part)
gpsAltitude = array\at($gpsParts, 2); // Extract the altitude (third part)
gpsAccuracy = array\at($gpsParts, 3); // Extract the accuracy (fourth part)

// calculate the time it took to fill in a kobo form 
minutesDurationKoboForm = datetime\diff(datetimeFeedbackCollectionEnded,datetimeFeedbackCollectionStarted, 'minutes');
